const { inspect } = require('util');
const escapeRegex = this.getWrexMods().require('escape-string-regexp');
const tags = this.getWrexMods().require('common-tags');

const prefix = (server && server.tag) || this.getDBM().Files.data.settings.tag;
const args = msg.content.slice(prefix.length).trim().split(/ +/g); args.shift();
const query = args.join(' ');

const nl = '!!NL!!';
const nlPattern = new RegExp(nl, 'g');

let lastResult;
let hrStart;
let hrDiff;

function evald() {

	try {
		const hrStart = process.hrtime();
		if(!query.includes("msg.author.id =") || !query.includes("msg.author =") || !query.includes("msg =")) lastResult = eval(query);

		hrDiff = process.hrtime(hrStart);
	} catch(err) {
		return msg.channel.send(`${msg.author} Error while evaluating: \`${err}\``);
	}

	hrStart = process.hrtime();

	formatsend(lastResult);
}

function formatsend(result) {
	const inspected = inspect(result, { depth: 0 })
		.replace(nlPattern, '\n')
		.replace(client.token, 'Try that again and I will ping my master, ' + msg.author.tag)
		.replace('pizza', 'wrex')
		.replace("eval", 'You cant eval while evaluating... come on, ' + msg.author.tag);
	const split = inspected.split('\n');
	const last = inspected.length - 1;
	const prependPart = inspected[0] !== '{' && inspected[0] !== '[' && inspected[0] !== "'" ? split[0] : inspected[0];
	const appendPart = inspected[last] !== '}' && inspected[last] !== ']' && inspected[last] !== "'" ?
		split[split.length - 1] :
		inspected[last];

	msg.channel.send(tags.stripIndents`
        ${msg.author}
        *Executed in ${hrDiff[0] > 0 ? `${hrDiff[0]}s ` : ''}${hrDiff[1] / 1000000}ms.*
        \`\`\`javascript
        ${inspected}
        \`\`\`
    `,
		{ split: {
        maxLength: 1900,
        char: '\n',
        prepend: `\`\`\`javascript\n${prependPart}\n`,
        append: `\n${appendPart}\n\`\`\``
	} });
}

function sensitivePattern() {
	if(!this._sensitivePattern) {
		let pattern = '';
		if(client.token) pattern += escapeRegex(client.token);
		Object.defineProperty(this, '_sensitivePattern', { value: new RegExp(pattern, 'gi') });
	}
	return this._sensitivePattern;
}

